mapper.update:
update hse_house
set is_deleted = 1
where id = #{id}

mapper.select:
select hc.name communityName ,
       hh.id as id,
       hh.name as name,
       build_area,
       unit_price,
       total_price,
       house_type_id,
       floor_id,
       direction_id,
       default_image_url,
       hh.create_time as createTime
from hse_house as hh left join hse_community hc on hh.community_id = hc.id


        and area_id = #{areaId}


        and plate_id = #{plateId}


        and house_type_id = #{houseTypeId}


        and floor_id = #{floorId}


        and build_structure_id = #{buildStructureId}


        and direction_id = #{directionId}


        and decoration_id = #{decorationId}


        and house_use_id = #{houseUseId}

    and hh.is_deleted = 0 and hh.status = 1 and hc.is_deleted = 0


    order by hh.id desc


    order by hh.total_price desc


    order by hh.create_time desc
